document.addEventListener("DOMContentLoaded",()=>{let iObserver=new IntersectionObserver(entries=>{entries.forEach(({target,isIntersecting})=>{target.classList[isIntersecting?"add":"remove"]("visible"),target.classList[isIntersecting?"remove":"add"]("hidden")})},{threshold:.5});document.querySelectorAll("h1, p, img, .footer-links").forEach((fadableElem,idx)=>setTimeout(()=>iObserver.observe(fadableElem),55*(idx+1)))});